import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faClock } from '@fortawesome/free-solid-svg-icons';

const API = 'http://localhost:5001/api';

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [selectedTaskId, setSelectedTaskId] = useState(null);
  const [timeInputs, setTimeInputs] = useState({});
  const [savedTimes, setSavedTimes] = useState({});
  const [allDayTasks, setAllDayTasks] = useState({});

  // Load tasks from API
  useEffect(() => {
    if (!token) return;
    axios.get(`${API}/tasks`, {
      headers: { Authorization: `Bearer ${token}` },
    }).then((res) => setTasks(res.data));
  }, [token]);

  // Deselect task when clicking outside
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (!e.target.closest('.task')) setSelectedTaskId(null);
    };
    window.addEventListener('click', handleClickOutside);
    return () => window.removeEventListener('click', handleClickOutside);
  }, []);

  // Simulated login
  const login = async () => {
    const res = await axios.post(`${API}/auth/login`, {
      email: 'test@example.com',
      password: 'taskpass123',
    });
    localStorage.setItem('token', res.data.token);
    setToken(res.data.token);
  };

  // Create a new task
  const addTask = async () => {
    await axios.post(`${API}/tasks`, { title }, {
      headers: { Authorization: `Bearer ${token}` },
    });
    const res = await axios.get(`${API}/tasks`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(res.data);
    setTitle('');
  };

  // Delete selected task
  const deleteTask = async () => {
    if (!selectedTaskId) return;
    await axios.delete(`${API}/tasks/${selectedTaskId}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(tasks.filter(task => task._id !== selectedTaskId));
    setSelectedTaskId(null);
  };

  // Toggle task status between pending and complete
  const toggleStatus = async (taskId, newStatus) => {
    const res = await axios.patch(`${API}/tasks/${taskId}`, { status: newStatus }, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(prev => prev.map(t => t._id === taskId ? { ...t, status: res.data.status } : t));
    // Reset any time or all-day mode if completed
    if (newStatus === 'complete') {
      setSavedTimes((prev) => {
        const copy = { ...prev };
        delete copy[taskId];
        return copy;
      });
      setAllDayTasks((prev) => {
        const copy = { ...prev };
        delete copy[taskId];
        return copy;
      });
    }
    setSelectedTaskId(null);
  };

  // When clicking on a task, toggle selection
  const handleTaskClick = (id) => {
    setSelectedTaskId((prev) => (prev === id ? null : id));
    setTimeInputs((prev) => ({ ...prev, [id]: { hour: '', minute: '', period: 'am' } }));
  };

  // Save a time or clear it if already set
  const saveTime = (taskId) => {
    if (savedTimes[taskId]) {
      setSavedTimes(prev => {
        const updated = { ...prev };
        delete updated[taskId];
        return updated;
      });
      setSelectedTaskId(null);
      return;
    }
    const { hour, minute, period } = timeInputs[taskId];
    if (hour && minute) {
      const timeStr = `${hour.padStart(2, '0')}:${minute.padStart(2, '0')} ${period.toUpperCase()}`;
      setSavedTimes(prev => ({ ...prev, [taskId]: timeStr }));
      setAllDayTasks(prev => {
        const copy = { ...prev };
        delete copy[taskId];
        return copy;
      });
      setSelectedTaskId(null);
    }
  };

  // Toggle all-day mode and clear time if set
  const markAllDay = (taskId) => {
    setAllDayTasks(prev => {
      const updated = { ...prev };
      if (updated[taskId]) {
        delete updated[taskId];
      } else {
        updated[taskId] = true;
        setSavedTimes((prev) => {
          const copy = { ...prev };
          delete copy[taskId];
          return copy;
        });
      }
      return updated;
    });
    setSelectedTaskId(null);
  };

  // Drag-and-drop handler
  const onDragEnd = (result) => {
    if (!result.destination) return;
    const newTasks = [...tasks];
    const [movedTask] = newTasks.splice(result.source.index, 1);
    newTasks.splice(result.destination.index, 0, movedTask);
    setTasks(newTasks);
  };

  if (!token) return <button onClick={login}>Login (Demo)</button>;

  return (
    <div className="container" style={{ padding: '20px' }}>
      <h2>Task Manager</h2>
      <input value={title} onChange={(e) => setTitle(e.target.value)} placeholder="New Task" />
      <button onClick={addTask}>Add Task</button>

      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="taskList">
          {(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef} className="task-list">
              {tasks.map((task, index) => (
                <Draggable key={task._id} draggableId={task._id} index={index}>
                  {(provided, snapshot) => {
                    const isSelected = selectedTaskId === task._id;
                    const isComplete = task.status === 'complete';

                    const classNames = [
                      'task',
                      isSelected && 'selected',
                      isComplete && 'completed',
                      allDayTasks[task._id] && 'all-day'
                    ].filter(Boolean).join(' ');

                    return (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        onClick={(e) => {
                          e.stopPropagation();
                          handleTaskClick(task._id);
                        }}
                        className={classNames}
                      >
                        <div className="task-header">
                          <div>
                            <strong>{task.title}</strong>
                            <br />
                            Status: {allDayTasks[task._id] ? 'all-day' : task.status}
                            {savedTimes[task._id] && (
                              <div className="time-display">‚è∞ {savedTimes[task._id]}</div>
                            )}
                          </div>
                          {/* Clock button (toggles selection and input view) */}
                          <FontAwesomeIcon
                            icon={faClock}
                            className="clock-icon"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleTaskClick(task._id);
                            }}
                          />
                        </div>

                        {/* Input/Control section */}
                        {isSelected && (
                          <>
                            <div className="task-actions">
                              {/* Delete button */}
                              <button onClick={(e) => { e.stopPropagation(); deleteTask(); }} className="btn delete">Delete</button>
                              {/* Toggle Complete/Incomplete */}
                              <button onClick={(e) => { e.stopPropagation(); toggleStatus(task._id, task.status === 'pending' ? 'complete' : 'pending'); }} className={`btn ${task.status === 'pending' ? 'complete' : 'incomplete'}`}>{task.status === 'pending' ? 'Complete' : 'Incomplete'}</button>
                            </div>

                            {/* Time input + buttons */}
                            {timeInputs[task._id] && (
                              <div className="time-entry">
                                {/* Hour input */}
                                <input type="number" min="1" max="12" placeholder="HH" value={timeInputs[task._id].hour} onChange={(e) => setTimeInputs((prev) => ({ ...prev, [task._id]: { ...prev[task._id], hour: e.target.value } }))} onClick={(e) => e.stopPropagation()} style={{ width: 50, padding: 4 }} />
                                :
                                {/* Minute input */}
                                <input type="number" min="0" max="59" placeholder="MM" value={timeInputs[task._id].minute} onChange={(e) => setTimeInputs((prev) => ({ ...prev, [task._id]: { ...prev[task._id], minute: e.target.value } }))} onClick={(e) => e.stopPropagation()} style={{ width: 50, padding: 4 }} />
                                {/* AM/PM select */}
                                <select value={timeInputs[task._id].period} onChange={(e) => setTimeInputs((prev) => ({ ...prev, [task._id]: { ...prev[task._id], period: e.target.value } }))} onClick={(e) => e.stopPropagation()} style={{ padding: 4 }}>
                                  <option value="am">AM</option>
                                  <option value="pm">PM</option>
                                </select>
                                {/* Enter time button */}
                                <button onClick={(e) => { e.stopPropagation(); saveTime(task._id); }} className="btn enter">Enter</button>
                                {/* All-day button */}
                                <button onClick={(e) => { e.stopPropagation(); markAllDay(task._id); }} className="btn all-day">All-Day</button>
                              </div>
                            )}
                          </>
                        )}
                      </div>
                    );
                  }}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
}

export default App;
