import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { Clock } from 'lucide-react';

const API = 'http://localhost:5001/api';

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [selectedTaskId, setSelectedTaskId] = useState(null);
  const [showClockOptions, setShowClockOptions] = useState(null);
  const [amPm, setAmPm] = useState('AM');

  useEffect(() => {
    if (!token) return;
    axios.get(`${API}/tasks`, {
      headers: { Authorization: `Bearer ${token}` },
    }).then((res) => setTasks(res.data));
  }, [token]);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (!e.target.closest('.task')) {
        setSelectedTaskId(null);
        setShowClockOptions(null);
      }
    };
    window.addEventListener('click', handleClickOutside);
    return () => window.removeEventListener('click', handleClickOutside);
  }, []);

  const login = async () => {
    const res = await axios.post(`${API}/auth/login`, {
      email: 'test@example.com',
      password: 'taskpass123',
    });
    localStorage.setItem('token', res.data.token);
    setToken(res.data.token);
  };

  const addTask = async () => {
    await axios.post(`${API}/tasks`, { title }, {
      headers: { Authorization: `Bearer ${token}` },
    });
    const res = await axios.get(`${API}/tasks`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(res.data);
    setTitle('');
  };

  const deleteTask = async () => {
    if (!selectedTaskId) return;
    await axios.delete(`${API}/tasks/${selectedTaskId}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(tasks.filter((task) => task._id !== selectedTaskId));
    setSelectedTaskId(null);
  };

  const toggleStatus = async (taskId, newStatus) => {
    const res = await axios.patch(`${API}/tasks/${taskId}`, { status: newStatus }, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks((prev) => prev.map((t) => t._id === taskId ? { ...t, status: res.data.status } : t));
    setSelectedTaskId(null);
  };

  const handleTaskClick = (id) => {
    setSelectedTaskId((prev) => (prev === id ? null : id));
  };

  const toggleAmPm = () => {
    setAmPm((prev) => (prev === 'AM' ? 'PM' : 'AM'));
  };

  const onDragEnd = (result) => {
    if (!result.destination) return;
    const newTasks = Array.from(tasks);
    const [movedTask] = newTasks.splice(result.source.index, 1);
    newTasks.splice(result.destination.index, 0, movedTask);
    setTasks(newTasks);
  };

  if (!token) return <button onClick={login}>Login (Demo)</button>;

  return (
    <div className="container" style={{ padding: '20px' }}>
      <h2>Task Manager</h2>
      <input
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="New Task"
      />
      <button onClick={addTask}>Add Task</button>

      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="taskList">
          {(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef} style={{ paddingTop: 10, paddingBottom: 10 }}>
              {tasks.map((task, index) => {
                const isSelected = selectedTaskId === task._id;
                const isComplete = task.status === 'complete';
                return (
                  <Draggable key={task._id} draggableId={task._id} index={index}>
                    {(provided, snapshot) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        onClick={(e) => {
                          e.stopPropagation();
                          handleTaskClick(task._id);
                        }}
                        className="task"
                        style={{
                          userSelect: 'none',
                          background: 'white',
                          padding: 15,
                          marginBottom: 10,
                          borderRadius: 6,
                          border: isSelected
                            ? '2px solid #007bff'
                            : isComplete
                            ? '2px solid green'
                            : '2px solid transparent',
                          boxShadow: snapshot.isDragging
                            ? '0 6px 16px rgba(0, 0, 0, 0.15)'
                            : '0 1px 4px rgba(0, 0, 0, 0.05)',
                          transition: 'all 0.2s ease',
                          cursor: 'grab',
                          display: 'flex',
                          justifyContent: 'space-between',
                          alignItems: 'center',
                          ...provided.draggableProps.style,
                        }}
                      >
                        <div>
                          <strong>{task.title}</strong>
                          <br />
                          Status: {task.status}
                          {isSelected && (
                            <div style={{ marginTop: 10 }}>
                              <button
                                onClick={(e) => {
                                  e.stopPropagation();
                                  deleteTask();
                                }}
                                style={{
                                  backgroundColor: 'red',
                                  color: 'white',
                                  border: 'none',
                                  padding: '6px 12px',
                                  borderRadius: 4,
                                  cursor: 'pointer',
                                  marginRight: 8,
                                }}
                              >
                                Delete
                              </button>
                              {task.status === 'pending' ? (
                                <button
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    toggleStatus(task._id, 'complete');
                                  }}
                                  style={{
                                    backgroundColor: 'green',
                                    color: 'white',
                                    border: 'none',
                                    padding: '6px 12px',
                                    borderRadius: 4,
                                    cursor: 'pointer',
                                  }}
                                >
                                  Complete
                                </button>
                              ) : (
                                <button
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    toggleStatus(task._id, 'pending');
                                  }}
                                  style={{
                                    backgroundColor: '#444',
                                    color: 'white',
                                    border: 'none',
                                    padding: '6px 12px',
                                    borderRadius: 4,
                                    cursor: 'pointer',
                                  }}
                                >
                                  Incomplete
                                </button>
                              )}
                            </div>
                          )}
                        </div>
                        <div>
                          <Clock size={20} style={{ cursor: 'pointer' }} onClick={(e) => {
                            e.stopPropagation();
                            setShowClockOptions(prev => prev === task._id ? null : task._id);
                          }} />
                          {showClockOptions === task._id && (
                            <div style={{ marginTop: 10 }}>
                              <button>Span</button>
                              <button style={{ marginLeft: 6 }}>Event</button>
                              <div style={{ marginTop: 8, display: 'flex', gap: '4px' }}>
                                <input
                                  type="number"
                                  placeholder="HH"
                                  min="1"
                                  max="12"
                                  style={{ width: 40 }}
                                />
                                :
                                <input
                                  type="number"
                                  placeholder="MM"
                                  min="0"
                                  max="59"
                                  style={{ width: 40 }}
                                />
                                -
                                <input
                                  type="number"
                                  placeholder="HH"
                                  min="1"
                                  max="12"
                                  style={{ width: 40 }}
                                />
                                :
                                <input
                                  type="number"
                                  placeholder="MM"
                                  min="0"
                                  max="59"
                                  style={{ width: 40 }}
                                />
                                <button
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    toggleAmPm();
                                  }}
                                >
                                  {amPm}
                                </button>
                              </div>
                            </div>
                          )}
                        </div>
                      </div>
                    )}
                  </Draggable>
                );
              })}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
}

export default App;
