// App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { FaClock } from 'react-icons/fa';

const API = 'http://localhost:5001/api';

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [selectedTaskId, setSelectedTaskId] = useState(null);
  const [showTimeInput, setShowTimeInput] = useState(null);
  const [timeData, setTimeData] = useState({});

  useEffect(() => {
    if (!token) return;
    axios
      .get(`${API}/tasks`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setTasks(res.data));
  }, [token]);

  const login = async () => {
    const res = await axios.post(`${API}/auth/login`, {
      email: 'test@example.com',
      password: 'taskpass123',
    });
    localStorage.setItem('token', res.data.token);
    setToken(res.data.token);
  };

  const addTask = async () => {
    await axios.post(
      `${API}/tasks`,
      { title },
      { headers: { Authorization: `Bearer ${token}` } }
    );
    const res = await axios.get(`${API}/tasks`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(res.data);
    setTitle('');
  };

  const handleTimeChange = (taskId, field, value) => {
    setTimeData(prev => ({
      ...prev,
      [taskId]: {
        ...prev[taskId],
        [field]: value
      }
    }));
  };

  const toggleAmPm = (taskId, field) => {
    setTimeData(prev => ({
      ...prev,
      [taskId]: {
        ...prev[taskId],
        [field]: prev[taskId]?.[field] === 'AM' ? 'PM' : 'AM'
      }
    }));
  };

  const onDragEnd = (result) => {
    if (!result.destination) return;
    const newTasks = Array.from(tasks);
    const [movedTask] = newTasks.splice(result.source.index, 1);
    newTasks.splice(result.destination.index, 0, movedTask);
    setTasks(newTasks);
  };

  if (!token) return <button onClick={login}>Login (Demo)</button>;

  return (
    <div className="container">
      <h2>Task Manager</h2>
      <input value={title} onChange={(e) => setTitle(e.target.value)} placeholder="New Task" />
      <button onClick={addTask}>Add Task</button>

      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="taskList">
          {(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef}>
              {tasks.map((task, index) => (
                <Draggable key={task._id} draggableId={task._id} index={index}>
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      className="task"
                      style={{
                        ...provided.draggableProps.style,
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center',
                      }}
                    >
                      <div>
                        <strong>{task.title}</strong><br />
                        Status: {task.status}
                        {showTimeInput === task._id && (
                          <div style={{ marginTop: 8 }}>
                            <button>Span</button>
                            <button>Event</button>
                            <div style={{ marginTop: 8 }}>
                              <input
                                type="number"
                                min="1"
                                max="12"
                                value={timeData[task._id]?.startHour || ''}
                                onChange={(e) => handleTimeChange(task._id, 'startHour', e.target.value)}
                                placeholder="HH"
                                style={{ width: 40 }}
                              />
                              :
                              <input
                                type="number"
                                min="0"
                                max="59"
                                value={timeData[task._id]?.startMin || ''}
                                onChange={(e) => handleTimeChange(task._id, 'startMin', e.target.value)}
                                placeholder="MM"
                                style={{ width: 40 }}
                              />
                              <button onClick={() => toggleAmPm(task._id, 'startAmPm')}>
                                {timeData[task._id]?.startAmPm || 'AM'}
                              </button>
                              -
                              <input
                                type="number"
                                min="1"
                                max="12"
                                value={timeData[task._id]?.endHour || ''}
                                onChange={(e) => handleTimeChange(task._id, 'endHour', e.target.value)}
                                placeholder="HH"
                                style={{ width: 40 }}
                              />
                              :
                              <input
                                type="number"
                                min="0"
                                max="59"
                                value={timeData[task._id]?.endMin || ''}
                                onChange={(e) => handleTimeChange(task._id, 'endMin', e.target.value)}
                                placeholder="MM"
                                style={{ width: 40 }}
                              />
                              <button onClick={() => toggleAmPm(task._id, 'endAmPm')}>
                                {timeData[task._id]?.endAmPm || 'AM'}
                              </button>
                            </div>
                          </div>
                        )}
                      </div>
                      <FaClock
                        onClick={() => setShowTimeInput(showTimeInput === task._id ? null : task._id)}
                        style={{ cursor: 'pointer' }}
                      />
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
}

export default App;
