import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faClock } from '@fortawesome/free-solid-svg-icons';

const API = 'http://localhost:5001/api';

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [selectedTaskId, setSelectedTaskId] = useState(null);
  const [timeInputs, setTimeInputs] = useState({});
  const [savedTimes, setSavedTimes] = useState({});
  const [allDayTasks, setAllDayTasks] = useState({});

  useEffect(() => {
    if (!token) return;
    axios
      .get(`${API}/tasks`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setTasks(res.data));
  }, [token]);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (!e.target.closest('.task')) {
        setSelectedTaskId(null);
      }
    };
    window.addEventListener('click', handleClickOutside);
    return () => window.removeEventListener('click', handleClickOutside);
  }, []);

  const login = async () => {
    const res = await axios.post(`${API}/auth/login`, {
      email: 'test@example.com',
      password: 'taskpass123',
    });
    localStorage.setItem('token', res.data.token);
    setToken(res.data.token);
  };

  const addTask = async () => {
    await axios.post(
      `${API}/tasks`,
      { title },
      { headers: { Authorization: `Bearer ${token}` } }
    );
    const res = await axios.get(`${API}/tasks`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(res.data);
    setTitle('');
  };

  const deleteTask = async () => {
    if (!selectedTaskId) return;
    await axios.delete(`${API}/tasks/${selectedTaskId}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(tasks.filter((task) => task._id !== selectedTaskId));
    setSelectedTaskId(null);
  };

  const toggleStatus = async (taskId, newStatus) => {
    const res = await axios.patch(
      `${API}/tasks/${taskId}`,
      { status: newStatus },
      { headers: { Authorization: `Bearer ${token}` } }
    );

    setTasks((prev) =>
      prev.map((t) => (t._id === taskId ? { ...t, status: res.data.status } : t))
    );
    setSelectedTaskId(null);
  };

  const handleTaskClick = (id) => {
    setSelectedTaskId((prev) => (prev === id ? null : id));
    setTimeInputs((prev) => ({
      ...prev,
      [id]: { hour: '', minute: '', period: 'am' }
    }));
  };

  const saveTime = (taskId) => {
    const { hour, minute, period } = timeInputs[taskId];
    if (hour && minute) {
      const timeStr = `${hour.padStart(2, '0')}:${minute.padStart(2, '0')} ${period.toUpperCase()}`;
      setSavedTimes((prev) => ({
        ...prev,
        [taskId]: timeStr
      }));
      setSelectedTaskId(null);
    }
  };

  const markAllDay = (taskId) => {
    setAllDayTasks((prev) => ({ ...prev, [taskId]: true }));
    setSelectedTaskId(null);
  };

  const onDragEnd = (result) => {
    if (!result.destination) return;
    const newTasks = Array.from(tasks);
    const [movedTask] = newTasks.splice(result.source.index, 1);
    newTasks.splice(result.destination.index, 0, movedTask);
    setTasks(newTasks);
  };

  if (!token) return <button onClick={login}>Login (Demo)</button>;

  return (
    <div className="container" style={{ padding: '20px' }}>
      <h2>Task Manager</h2>
      <input
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="New Task"
      />
      <button onClick={addTask}>Add Task</button>

      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="taskList">
          {(provided) => (
            <div
              {...provided.droppableProps}
              ref={provided.innerRef}
              style={{ paddingTop: 10, paddingBottom: 10 }}
            >
              {tasks.map((task, index) => (
                <Draggable key={task._id} draggableId={task._id} index={index}>
                  {(provided, snapshot) => {
                    const isSelected = selectedTaskId === task._id;
                    const isComplete = task.status === 'complete';

                    const baseStyle = {
                      userSelect: 'none',
                      background: allDayTasks[task._id] ? '#f9f0ff' : 'white',
                      padding: 15,
                      marginBottom: 10,
                      borderRadius: 6,
                      border: isSelected
                        ? '2px solid #007bff'
                        : isComplete
                        ? '2px solid green'
                        : allDayTasks[task._id]
                        ? '2px solid purple'
                        : '2px solid transparent',
                      boxShadow: snapshot.isDragging
                        ? '0 6px 16px rgba(0, 0, 0, 0.15)'
                        : '0 1px 4px rgba(0, 0, 0, 0.05)',
                      transition: 'all 0.2s ease',
                      cursor: 'grab',
                      ...provided.draggableProps.style,
                    };

                    return (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        onClick={(e) => {
                          e.stopPropagation();
                          handleTaskClick(task._id);
                        }}
                        className="task"
                        style={baseStyle}
                      >
                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                          <div>
                            <strong>{task.title}</strong>
                            <br />
                            Status: {allDayTasks[task._id] ? 'all-day' : task.status}
                            {savedTimes[task._id] && (
                              <div style={{ marginTop: 4, fontStyle: 'italic' }}>
                                ‚è∞ {savedTimes[task._id]}
                              </div>
                            )}
                          </div>
                          <FontAwesomeIcon
                            icon={faClock}
                            className="clock-icon"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleTaskClick(task._id);
                            }}
                          />
                        </div>

                        {isSelected && (
                          <>
                            <div style={{ marginTop: 10 }}>
                              <button
                                onClick={(e) => {
                                  e.stopPropagation();
                                  deleteTask();
                                }}
                                style={{
                                  backgroundColor: 'red',
                                  color: 'white',
                                  border: 'none',
                                  padding: '6px 12px',
                                  borderRadius: 4,
                                  cursor: 'pointer',
                                  marginRight: 8,
                                }}
                              >
                                Delete
                              </button>

                              {task.status === 'pending' ? (
                                <button
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    toggleStatus(task._id, 'complete');
                                  }}
                                  style={{
                                    backgroundColor: 'green',
                                    color: 'white',
                                    border: 'none',
                                    padding: '6px 12px',
                                    borderRadius: 4,
                                    cursor: 'pointer',
                                  }}
                                >
                                  Complete
                                </button>
                              ) : (
                                <button
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    toggleStatus(task._id, 'pending');
                                  }}
                                  style={{
                                    backgroundColor: '#444',
                                    color: 'white',
                                    border: 'none',
                                    padding: '6px 12px',
                                    borderRadius: 4,
                                    cursor: 'pointer',
                                  }}
                                >
                                  Incomplete
                                </button>
                              )}
                            </div>

                            {timeInputs[task._id] && (
                              <div style={{ marginTop: 10, display: 'flex', gap: 8, alignItems: 'center' }}>
                                <input
                                  type="number"
                                  min="1"
                                  max="12"
                                  placeholder="HH"
                                  value={timeInputs[task._id].hour}
                                  onChange={(e) =>
                                    setTimeInputs((prev) => ({
                                      ...prev,
                                      [task._id]: { ...prev[task._id], hour: e.target.value },
                                    }))
                                  }
                                  onClick={(e) => e.stopPropagation()}
                                  style={{ width: 50, padding: 4 }}
                                />
                                :
                                <input
                                  type="number"
                                  min="0"
                                  max="59"
                                  placeholder="MM"
                                  value={timeInputs[task._id].minute}
                                  onChange={(e) =>
                                    setTimeInputs((prev) => ({
                                      ...prev,
                                      [task._id]: { ...prev[task._id], minute: e.target.value },
                                    }))
                                  }
                                  onClick={(e) => e.stopPropagation()}
                                  style={{ width: 50, padding: 4 }}
                                />
                                <select
                                  value={timeInputs[task._id].period}
                                  onChange={(e) =>
                                    setTimeInputs((prev) => ({
                                      ...prev,
                                      [task._id]: { ...prev[task._id], period: e.target.value },
                                    }))
                                  }
                                  onClick={(e) => e.stopPropagation()}
                                  style={{ padding: 4 }}
                                >
                                  <option value="am">AM</option>
                                  <option value="pm">PM</option>
                                </select>
                                <button
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    saveTime(task._id);
                                  }}
                                  style={{
                                    backgroundColor: '#007bff',
                                    color: 'white',
                                    border: 'none',
                                    padding: '6px 12px',
                                    borderRadius: 4,
                                    cursor: 'pointer',
                                  }}
                                >
                                  Enter
                                </button>
                                <button
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    markAllDay(task._id);
                                  }}
                                  style={{
                                    backgroundColor: 'purple',
                                    color: 'white',
                                    border: 'none',
                                    padding: '6px 12px',
                                    borderRadius: 4,
                                    cursor: 'pointer',
                                  }}
                                >
                                  All-Day
                                </button>
                              </div>
                            )}
                          </>
                        )}
                      </div>
                    );
                  }}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
}

export default App;
