import React, { useEffect, useState } from 'react';
import axios from 'axios';

const API = 'http://localhost:5001/api';

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [selectedTaskId, setSelectedTaskId] = useState(null);

  useEffect(() => {
    if (!token) return;
    axios.get(`${API}/tasks`, {
      headers: { Authorization: `Bearer ${token}` }
    }).then(res => setTasks(res.data));

    // Deselect task if clicked outside
    const handleClickOutside = (e) => {
      if (!e.target.closest('.task')) {
        setSelectedTaskId(null);
      }
    };

    window.addEventListener('click', handleClickOutside);
    return () => window.removeEventListener('click', handleClickOutside);
  }, [token]);

  const login = async () => {
    const res = await axios.post(`${API}/auth/login`, {
      email: 'test@example.com',
      password: 'taskpass123'
    });
    localStorage.setItem('token', res.data.token);
    setToken(res.data.token);
  };

  const addTask = async () => {
    await axios.post(`${API}/tasks`, { title }, {
      headers: { Authorization: `Bearer ${token}` }
    });
    const res = await axios.get(`${API}/tasks`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    setTasks(res.data);
    setTitle('');
  };

  const deleteTask = async (taskId) => {
    await axios.delete(`${API}/tasks/${taskId}`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    const res = await axios.get(`${API}/tasks`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    setTasks(res.data);
    setSelectedTaskId(null);
  };

  if (!token) return <button onClick={login}>Login (Demo)</button>;

  return (
    <div className="container">
      <h2>Task Manager</h2>
      <input
        value={title}
        onChange={e => setTitle(e.target.value)}
        placeholder="New Task"
      />
      <button onClick={addTask}>Add Task</button>

      {tasks.map(task => (
        <div
          key={task._id}
          className="task"
          onClick={(e) => {
            e.stopPropagation(); // prevent deselection
            setSelectedTaskId(task._id);
          }}
          style={{
            border: selectedTaskId === task._id ? '3px solid #4A90E2' : '1px solid #ccc',
            padding: '10px',
            marginTop: '10px',
            backgroundColor: '#fff',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          <strong>{task.title}</strong><br />
          Status: {task.status}
          {selectedTaskId === task._id && (
            <div>
              <button
                style={{
                  marginTop: '10px',
                  backgroundColor: '#e74c3c',
                  color: '#fff',
                  border: 'none',
                  padding: '6px 10px',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
                onClick={(e) => {
                  e.stopPropagation(); // prevent reselect/deselect
                  deleteTask(task._id);
                }}
              >
                Delete
              </button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default App;
