import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const API = 'http://localhost:5001/api';

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [selectedTaskId, setSelectedTaskId] = useState(null);

  useEffect(() => {
    if (!token) return;
    axios
      .get(`${API}/tasks`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setTasks(res.data));
  }, [token]);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (!e.target.closest('.task')) {
        setSelectedTaskId(null);
      }
    };
    window.addEventListener('click', handleClickOutside);
    return () => window.removeEventListener('click', handleClickOutside);
  }, []);

  const login = async () => {
    const res = await axios.post(`${API}/auth/login`, {
      email: 'test@example.com',
      password: 'taskpass123',
    });
    localStorage.setItem('token', res.data.token);
    setToken(res.data.token);
  };

  const addTask = async () => {
    await axios.post(
      `${API}/tasks`,
      { title },
      { headers: { Authorization: `Bearer ${token}` } }
    );
    const res = await axios.get(`${API}/tasks`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(res.data);
    setTitle('');
  };

  const deleteTask = async () => {
    if (!selectedTaskId) return;
    await axios.delete(`${API}/tasks/${selectedTaskId}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(tasks.filter((task) => task._id !== selectedTaskId));
    setSelectedTaskId(null);
  };

  const toggleStatus = async () => {
    const task = tasks.find((t) => t._id === selectedTaskId);
    if (!task) return;

    const newStatus = task.status === 'pending' ? 'completed' : 'pending';

    await axios.patch(
      `${API}/tasks/${selectedTaskId}`,
      { status: newStatus },
      { headers: { Authorization: `Bearer ${token}` } }
    );

    const res = await axios.get(`${API}/tasks`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(res.data);
    setSelectedTaskId(null);
  };

  const handleTaskClick = (id) => {
    setSelectedTaskId((prev) => (prev === id ? null : id));
  };

  const onDragEnd = (result) => {
    if (!result.destination) return;
    const newTasks = Array.from(tasks);
    const [movedTask] = newTasks.splice(result.source.index, 1);
    newTasks.splice(result.destination.index, 0, movedTask);
    setTasks(newTasks);
  };

  if (!token) return <button onClick={login}>Login (Demo)</button>;

  return (
    <div className="container" style={{ padding: '20px' }}>
      <h2>Task Manager</h2>
      <input
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="New Task"
      />
      <button onClick={addTask}>Add Task</button>

      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="taskList">
          {(provided) => (
            <div
              {...provided.droppableProps}
              ref={provided.innerRef}
              style={{ paddingTop: 10, paddingBottom: 10 }}
            >
              {tasks.map((task, index) => {
                const isSelected = selectedTaskId === task._id;
                const isCompleted = task.status === 'completed';

                const baseStyle = {
                  userSelect: 'none',
                  background: 'white',
                  padding: 12,
                  marginBottom: 10,
                  borderRadius: 6,
                  border: isSelected
                    ? '2px solid #007bff'
                    : isCompleted
                    ? '2px solid green'
                    : '2px solid transparent',
                  boxShadow: '0 1px 4px rgba(0, 0, 0, 0.05)',
                  cursor: 'grab',
                  transition: 'all 0.2s ease',
                };

                return (
                  <Draggable key={task._id} draggableId={task._id} index={index}>
                    {(provided, snapshot) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        onClick={(e) => {
                          e.stopPropagation();
                          handleTaskClick(task._id);
                        }}
                        className="task"
                        style={{
                          ...baseStyle,
                          ...provided.draggableProps.style,
                          boxShadow: snapshot.isDragging
                            ? '0 6px 16px rgba(0, 0, 0, 0.2)'
                            : baseStyle.boxShadow,
                        }}
                      >
                        <strong>{task.title}</strong>
                        <br />
                        Status: {task.status}
                        {isSelected && (
                          <div style={{ marginTop: 8, display: 'flex', gap: 8 }}>
                            <button
                              onClick={deleteTask}
                              style={{
                                backgroundColor: 'red',
                                color: 'white',
                                border: 'none',
                                padding: '6px 12px',
                                borderRadius: 4,
                                cursor: 'pointer',
                              }}
                            >
                              Delete
                            </button>
                            <button
                              onClick={toggleStatus}
                              style={{
                                backgroundColor: isCompleted ? '#666' : 'green',
                                color: 'white',
                                border: 'none',
                                padding: '6px 12px',
                                borderRadius: 4,
                                cursor: 'pointer',
                              }}
                            >
                              {isCompleted ? 'Incomplete' : 'Complete'}
                            </button>
                          </div>
                        )}
                      </div>
                    )}
                  </Draggable>
                );
              })}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
}

export default App;
