import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faClock } from '@fortawesome/free-solid-svg-icons';

const API = 'http://localhost:5001/api';

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [selectedTaskId, setSelectedTaskId] = useState(null);
  const [timeInputs, setTimeInputs] = useState({});
  const [savedTimes, setSavedTimes] = useState({});
  const [allDayTasks, setAllDayTasks] = useState({});

  useEffect(() => {
    if (!token) return;
    axios.get(`${API}/tasks`, {
      headers: { Authorization: `Bearer ${token}` },
    }).then((res) => setTasks(res.data));
  }, [token]);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (!e.target.closest('.task')) setSelectedTaskId(null);
    };
    window.addEventListener('click', handleClickOutside);
    return () => window.removeEventListener('click', handleClickOutside);
  }, []);

  const login = async () => {
    const res = await axios.post(`${API}/auth/login`, {
      email: 'test@example.com',
      password: 'taskpass123',
    });
    localStorage.setItem('token', res.data.token);
    setToken(res.data.token);
  };

  const addTask = async () => {
    await axios.post(`${API}/tasks`, { title }, {
      headers: { Authorization: `Bearer ${token}` },
    });
    const res = await axios.get(`${API}/tasks`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(res.data);
    setTitle('');
  };

  const deleteTask = async () => {
    if (!selectedTaskId) return;
    await axios.delete(`${API}/tasks/${selectedTaskId}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(tasks.filter(task => task._id !== selectedTaskId));
    setSelectedTaskId(null);
  };

  const toggleStatus = async (taskId, newStatus) => {
    const res = await axios.patch(`${API}/tasks/${taskId}`, { status: newStatus }, {
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(prev => prev.map(t => t._id === taskId ? { ...t, status: res.data.status } : t));
    setAllDayTasks(prev => {
      const updated = { ...prev };
      delete updated[taskId];
      return updated;
    });
    setSavedTimes(prev => {
      const updated = { ...prev };
      delete updated[taskId];
      return updated;
    });
    setSelectedTaskId(null);
  };

  const handleTaskClick = (id) => {
    setSelectedTaskId((prev) => (prev === id ? null : id));
    setTimeInputs((prev) => ({ ...prev, [id]: { hour: '', minute: '', period: 'am' } }));
  };

  const saveTime = (taskId) => {
    if (savedTimes[taskId]) {
      setSavedTimes(prev => {
        const updated = { ...prev };
        delete updated[taskId];
        return updated;
      });
      return setSelectedTaskId(null);
    }
    const { hour, minute, period } = timeInputs[taskId];
    if (hour && minute) {
      const timeStr = `${hour.padStart(2, '0')}:${minute.padStart(2, '0')} ${period.toUpperCase()}`;
      setSavedTimes(prev => ({ ...prev, [taskId]: timeStr }));
      setAllDayTasks(prev => {
        const updated = { ...prev };
        delete updated[taskId];
        return updated;
      });
      setSelectedTaskId(null);
    }
  };

  const markAllDay = (taskId) => {
    const newAllDay = !allDayTasks[taskId];
    setAllDayTasks(prev => ({ ...prev, [taskId]: newAllDay }));
    if (newAllDay) {
      setSavedTimes(prev => {
        const updated = { ...prev };
        delete updated[taskId];
        return updated;
      });
    }
    setSelectedTaskId(null);
  };

  const onDragEnd = (result) => {
    if (!result.destination) return;
    const newTasks = [...tasks];
    const [movedTask] = newTasks.splice(result.source.index, 1);
    newTasks.splice(result.destination.index, 0, movedTask);
    setTasks(newTasks);
  };

  if (!token) return <button onClick={login}>Login (Demo)</button>;

  return (
    <div className="container">
      <h2>Task Manager</h2>
      <input value={title} onChange={(e) => setTitle(e.target.value)} placeholder="New Task" />
      <button onClick={addTask}>Add Task</button>

      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="taskList">
          {(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef} className="task-list">
              {tasks.map((task, index) => (
                <Draggable key={task._id} draggableId={task._id} index={index}>
                  {(provided, snapshot) => {
                    const isSelected = selectedTaskId === task._id;
                    const isComplete = task.status === 'complete';

                    let taskClass = 'task';
                    if (allDayTasks[task._id]) taskClass += ' all-day';
                    if (isComplete) taskClass += ' completed';
                    if (isSelected) taskClass += ' selected';

                    return (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        onClick={(e) => {
                          e.stopPropagation();
                          handleTaskClick(task._id);
                        }}
                        className={taskClass}
                        style={provided.draggableProps.style}
                      >
                        <div className="task-header">
                          <div>
                            <strong>{task.title}</strong><br />
                            Status: {allDayTasks[task._id] ? 'all-day' : task.status}
                            {savedTimes[task._id] && <div className="time-display">‚è∞ {savedTimes[task._id]}</div>}
                          </div>
                          <FontAwesomeIcon icon={faClock} className="clock-icon" onClick={(e) => { e.stopPropagation(); handleTaskClick(task._id); }} />
                        </div>

                        {isSelected && (
                          <>
                            <div className="task-actions">
                              <button className="btn delete" onClick={(e) => { e.stopPropagation(); deleteTask(); }}>Delete</button>
                              <button className={`btn ${task.status === 'pending' ? 'complete' : 'incomplete'}`} onClick={(e) => { e.stopPropagation(); toggleStatus(task._id, task.status === 'pending' ? 'complete' : 'pending'); }}>{task.status === 'pending' ? 'Complete' : 'Incomplete'}</button>
                            </div>

                            <div className="time-entry">
                              <input type="number" min="1" max="12" placeholder="HH" value={timeInputs[task._id]?.hour || ''} onChange={(e) => setTimeInputs((prev) => ({ ...prev, [task._id]: { ...prev[task._id], hour: e.target.value } }))} onClick={(e) => e.stopPropagation()} />
                              :
                              <input type="number" min="0" max="59" placeholder="MM" value={timeInputs[task._id]?.minute || ''} onChange={(e) => setTimeInputs((prev) => ({ ...prev, [task._id]: { ...prev[task._id], minute: e.target.value } }))} onClick={(e) => e.stopPropagation()} />
                              <select value={timeInputs[task._id]?.period || 'am'} onChange={(e) => setTimeInputs((prev) => ({ ...prev, [task._id]: { ...prev[task._id], period: e.target.value } }))} onClick={(e) => e.stopPropagation()}>
                                <option value="am">AM</option>
                                <option value="pm">PM</option>
                              </select>
                              <button className="btn enter" onClick={(e) => { e.stopPropagation(); saveTime(task._id); }}>Enter</button>
                              <button className="btn all-day" onClick={(e) => { e.stopPropagation(); markAllDay(task._id); }}>All-Day</button>
                            </div>
                          </>
                        )}
                      </div>
                    );
                  }}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
}

export default App;
